    "Black-and-Red",
    "Blue-Light",
    "Violet-Light",
    "Green-Light",
    "Pink-Light",
    "Orange-Light",
    "Red-Light",
    "GX-Classic",
    "Ultraviolet",
    "Sub-Zero",
    "Frutti-Di-Mare",
    "Purple-Haze",
    "Vaporwave",
    "Rose-Quartz",
    "Coming-Soon",
    "Hackerman",
    "Lambda",
    "After-Eight",
    "Pay-To-Win",
    "White-Wolf",
]
@var number videos-per-row "ðŸŽ¥ Videos per row" 0
@var checkbox no-profile-pictures "ðŸ‘¤ Video grid: No avatars" 0
@var checkbox font-size "ðŸ—› Video grid: Smaller font" 0;
@var checkbox full-titles "ðŸŽ¥ Show full video titles" 0
@var checkbox no-shorts "ðŸ©³ No Shorts" 0
@var checkbox search-dismissible "ðŸ”Ž No irrelevant search results" 0
@var checkbox grid-search-results "ðŸ”Ž Search results in Grid view" 0
@var checkbox no-right-sidebar "â€ƒâ€ƒNo right sidebar" 0
@var checkbox more-animations "ðŸŽžï¸ More animations" 0
@var checkbox compact-sidebar "âœ‚ï¸ Compact left sidebar" 0
@var checkbox compact-header-bar "âœ‚ï¸ Compact header bar" 0
@var checkbox no-recomendations-bar "âŒ No topic bar" 0
@var checkbox no-yt-posts "âŒ No Latest YouTube Posts" 0
@var checkbox expanded-comments "ðŸ’¬ Auto-expand comments" 0
@var checkbox sidebar-comments "ðŸ’¬ Show comments in sidebar" 0
@var checkbox pin-video-on-scroll "ðŸ“ Pin video while scrolling" 0
@var number pinned-video-width "â€ƒâ€ƒVideo width" [480, 0, 9999, 1, 'px']
@var select pinned-video-position "â€ƒâ€ƒVideo position" ["Bottom-right", "Bottom-center", "Bottom-left", "Center-right", "Center-left", "Top-right", "Top-center", "Top-left"]
@var number pinned-video-subs-size "â€ƒâ€ƒSubtitles size" [1.4, 0, 9999, 0.1, 'rem']
@var checkbox ignore-paused-videos "â€ƒâ€ƒIgnore paused videos" 0
@var checkbox theater "ðŸŽ­ Fullscreen Theater mode" 0
@var checkbox ambient-mode "ðŸ’¡ Ambient mode on Theater mode" 0
@var checkbox no-end-cards "ðŸ’³ No end cards (unless hovering)" 0
@var checkbox subscribe-color "ðŸ”´ Red subscribe button" 0
@var checkbox share "ðŸ” Watch page: Share button" 1
@var checkbox download "â¬‡ï¸  Watch page: Download button" 1
@var checkbox clip "âœ‚ï¸ Watch page: Clip button" 1
@var checkbox thanks "ðŸ’° Watch page: Thanks button" 1
@var checkbox save "ðŸ’¾ Watch page: Save button" 1
@var checkbox compact-buttons "âœ‚ï¸ Watch page: Compact buttons" 0
@var checkbox no-shorts-button "âŒ Left sidebar: No Shorts button" 0
@var checkbox no-explore "âŒ Left sidebar: No Explore" 0
@var checkbox no-more-from-yt "âŒ Left sidebar: No More from YouTube" 0
@var checkbox no-watch-videos "ðŸ‘€ No watched videos" 0
@var checkbox no-watch-videos-2 "â€ƒâ€ƒHomepage" 0
@var checkbox no-watch-videos-3 "â€ƒâ€ƒSubscriptions" 0
@var checkbox no-watch-videos-4 "â€ƒâ€ƒRelated videos" 0
@var checkbox no-watch-videos-5 "â€ƒâ€ƒSearch page" 0
@var checkbox no-watch-videos-6 "â€ƒâ€ƒChannel Videos tab" 0
@var checkbox only-fully-watch-vid "â€ƒâ€ƒFully-watched ones only" 0
@var checkbox no-mixes "ðŸŽµ No Mixes: Homepage" 0
@var checkbox no-mixes-2 "ðŸŽµ No Mixes: Related videos" 0
@var checkbox no-mixes-3 "ðŸŽµ No Mixes: Search page" 0
@var checkbox no-upcoming "ðŸ“† No upcomings: Homepage" 0
@var checkbox no-upcoming-2 "ðŸ“† No upcomings: Subscriptions" 0
@var checkbox no-upcoming-3 "ðŸ“† No upcomings: Search page" 0
@var checkbox no-live-streams "ðŸ”´ No lives: Homepage" 0
@var checkbox no-live-streams-2 "ðŸ”´ No lives: Subscriptions" 0
@var checkbox no-live-streams-3 "ðŸ”´ No lives: Related videos" 0
@var checkbox no-live-streams-4 "ðŸ”´ No lives: Search page" 0
@var checkbox less-dark "ðŸ”† Lighter dark mode" 0
==/UserStyle== */

ytd-rich-item-renderer[rendered-from-rich-grid]:not([is-shorts-grid]) {
    margin-left: calc(var(--ytd-rich-grid-item-margin, 16px) / 2) !important;
    margin-right: calc(var(--ytd-rich-grid-item-margin, 16px) / 2) !important;
  }
  body[dir="ltr"] #contents.ytd-rich-grid-renderer {
    padding-left: calc(var(--ytd-rich-grid-gutter-margin) * 2) !important;
  }
  body[dir="rtl"] #contents.ytd-rich-grid-renderer {
    padding-right: calc(var(--ytd-rich-grid-gutter-margin) * 2) !important;
  }
  body[dir="ltr"] #contents.ytd-rich-grid-renderer > :not(ytd-rich-item-renderer) {
    margin-left: calc(var(--ytd-rich-grid-gutter-margin) * -1) !important;
    margin-right: var(--ytd-rich-grid-gutter-margin) !important;
  }
  body[dir="rtl"] #contents.ytd-rich-grid-renderer > :not(ytd-rich-item-renderer) {
    margin-left: var(--ytd-rich-grid-gutter-margin) !important;
    margin-right: calc(var(--ytd-rich-grid-gutter-margin) * -1) !important;
  }
  :root {
    --main-color: 142, 192, 124;
    --main-text: 235, 219, 178;
    --main-background: 40, 40, 40;
    --button-color: 230, 230, 230;
    --button-solid-color: #3b3b3b;
    --filled-button-hover-color: #7aa26b;
    --red: #fb4934;
    --green: #8ec07c;
    --filter: invert(85%) sepia(40%) saturate(189%) hue-rotate(4deg) brightness(98%) contrast(91%);
    --main-color-inverted: #713f83;
    --invert-filter: invert(100%);
  }

        html,
html[dark],
html [dark] {
            --yt-spec-text-primary: rgb(var(--main-text)) !important;
            --yt-spec-touch-response: rgb(var(--main-text)) !important;
            --yt-spec-touch-response-inverse: currentcolor !important;
            --yt-spec-base-background: rgb(var(--main-background)) !important;
            --yt-spec-raised-background: rgb(var(--main-background)) !important;
            --yt-frosted-glass-desktop: rgb(var(--main-background)) !important;
            --yt-spec-additive-background: rgba(var(--button-color), var(--button-opacity, 0.10)) !important;
            --yt-spec-badge-chip-background: rgba(var(--button-color), var(--button-opacity, 0.10)) !important;
            --yt-spec-outline: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
            --yt-spec-outline-opaque: var(--yt-spec-outline) !important;
            --yt-spec-outline-inverse-opaque: rgb(var(--main-color)) !important;
            --yt-spec-10-percent-layer: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
            --yt-spec-text-secondary: rgba(var(--main-text), 0.68) !important;
            --yt-spec-button-chip-background-hover: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
            --yt-spec-icon-disabled: rgba(var(--main-text), 0.38) !important;
            --yt-spec-suggested-action: rgba(var(--main-color), 0.30) !important;
            --yt-spec-suggested-action-inverse: rgba(var(--white, var(--main-background)), 0.2) !important;
            --yt-spec-static-overlay-touch-response-inverse: currentcolor !important;
            --yt-spec-static-overlay-background-brand: var(--red) !important;
            --yt-spec-static-brand-red: rgb(var(--main-color)) !important;
            --yt-spec-commerce-filled-hover: var(--filled-button-hover-color) !important;
            --yt-spec-mono-tonal-hover: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
            --yt-spec-text-disabled: rgba(var(--main-text), 0.38) !important;
            --yt-spec-wordmark-text: rgb(var(--main-text)) !important;
            --yt-spec-brand-icon-active: rgb(var(--main-text)) !important;
            --yt-spec-icon-active-other: rgb(var(--main-text)) !important;
            --yt-spec-call-to-action: rgb(var(--main-color)) !important;
            --yt-spec-call-to-action-inverse: rgb(var(--black, var(--white, var(--main-background)))) !important;
            --yt-spec-call-to-action-faded: rgba(var(--main-color), 0.30) !important;
            --yt-spec-themed-blue: rgb(var(--main-color)) !important;
            --yt-spec-themed-green: var(--green) !important;
            --yt-spec-menu-background: var(--button-solid-color) !important;
            --yt-spec-brand-background-primary: rgb(var(--main-background)) !important;
            --yt-spec-general-background-a: rgb(var(--main-background)) !important;
            --yt-spec-general-background-b: rgb(var(--main-background)) !important;
            --yt-spec-general-background-c: rgb(var(--main-background)) !important;
            --yt-spec-static-brand-black: rgb(var(--main-background)) !important;
            --yt-spec-static-overlay-call-to-action: currentcolor !important;
            --yt-spec-dark-blue: rgb(var(--main-color)) !important;
            --yt-spec-light-blue: rgb(var(--main-color)) !important;
            --yt-spec-grey-5: rgba(var(--main-text), 0.38) !important;
            --yt-spec-brand-background-solid: rgb(var(--main-background)) !important;
            --yt-emoji-picker-search-background-color: rgba(var(--button-color), var(--button-opacity, 0.10)) !important;
            --yt-emoji-picker-search-color: rgb(var(--main-text)) !important;
            --yt-emoji-picker-search-placeholder-color: rgba(var(--main-text), 0.68) !important;
            --yt-live-chat-primary-text-color: rgb(var(--main-text)) !important;
            --yt-live-chat-secondary-text-color: rgba(var(--main-text), 0.68) !important;
            --yt-live-chat-secondary-background-color: rgba(var(--button-color), var(--button-opacity, 0.10)) !important;
            --yt-live-chat-text-input-field-inactive-underline-color: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
            --yt-live-chat-tertiary-text-color: rgba(var(--main-text), 0.68) !important;
            --yt-live-chat-vem-background-color: var(--button-solid-color) !important;
            --yt-live-chat-background-color: rgb(var(--main-background)) !important;
            --yt-live-chat-banner-gradient-scrim: linear-gradient(rgb(var(--main-background)), transparent) !important;
            --yt-live-chat-action-panel-gradient-scrim: linear-gradient( to top, rgb(var(--main-background)), transparent) !important;
            --yt-live-chat-toast-background-color: var(--button-solid-color) !important;
            --yt-live-chat-toast-action-color: rgb(var(--main-color)) !important;
            --yt-live-chat-toast-text-color: rgb(var(--main-text)) !important;
            --yt-live-chat-product-picker-hover-color: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
            --yt-live-chat-sub-panel-background-color-transparent: rgb(var(--main-background)) !important;
            --yt-live-chat-action-panel-background-color: rgb(var(--main-background)) !important;
            --yt-live-chat-action-panel-background-color-transparent: rgb(var(--main-background)) !important;
            --yt-live-chat-dialog-background-color: rgb(var(--main-background)) !important;
            --yt-live-chat-author-chip-verified-background-color: rgb(var(--main-color)) !important;
            --yt-live-chat-author-chip-verified-text-color: rgb(var(--black, var(--white, var(--main-background)))) !important;
            --yt-live-chat-dialog-text-color: rgb(var(--main-text)) !important;
            --yt-live-chat-header-background-color: rgb(var(--main-background)) !important;
            --yt-live-chat-slider-active-color: rgb(var(--main-color)) !important;
            --yt-live-chat-slider-container-color: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
            --yt-live-chat-sponsor-color: var(--green) !important;
            --yt-live-chat-moderator-color: rgb(var(--main-color)) !important;
            --yt-live-chat-slider-markers-color: rgb(var(--main-text)) !important;
            --yt-live-chat-shimmer-linear-gradient: linear-gradient( 0deg, rgba(0, 0, 0, .1) 40%, rgba(var(--button-color), var(--button-opacity, 0.10)), rgba(0, 0, 0, 0.1) 60%) !important;
            --yt-live-chat-deleted-message-color: rgba(var(--main-text), 0.68) !important;
            --yt-live-chat-shimmer-background-color: rgba(0, 0, 0, 0.3) !important;
            --yt-live-chat-overlay-color: rgba(0, 0, 0, 0.3) !important;
            --yt-live-chat-button-dark-text-color: rgb(var(--main-text)) !important;
            --yt-live-chat-mode-change-background-color: rgba(var(--button-color), var(--button-opacity, 0.10)) !important;
            --yt-live-chat-text-input-field-placeholder-color: rgba(var(--main-text), 0.68) !important;
            --yt-compact-link-icon-color: rgb(var(--main-text)) !important;
            --yt-deprecated-blue: rgb(var(--main-color)) !important;
            --paper-radio-button-checked-color: rgb(var(--main-color)) !important;
            --paper-radio-button-checked-ink-color: rgb(var(--main-color)) !important;
            --paper-radio-button-unchecked-color: rgb(var(--main-text)) !important;
            --paper-radio-button-unchecked-ink-color: rgb(var(--main-text)) !important;
            --paper-toggle-button-checked-bar-color: rgb(var(--main-color)) !important;
            --paper-toggle-button-unchecked-bar-color: rgb(var(--main-text)) !important;
            --paper-toggle-button-checked-button-color: rgb(var(--main-color)) !important;
            --paper-toggle-button-checked-ink-color: rgb(var(--main-color)) !important;
            --paper-toggle-button-unchecked-button-color: rgb(var(--main-text)) !important;
            --paper-toggle-button-unchecked-bar-opacity: 0.38 !important;
            --paper-toggle-button-checked-bar-opacity: 0.38 !important;
            --paper-checkbox-checked-color: rgb(var(--main-color)) !important;
            --paper-checkbox-checked-ink-color: rgb(var(--main-color)) !important;
            --paper-checkbox-unchecked-color: rgb(var(--main-text)) !important;
            --paper-checkbox-unchecked-ink-color: rgb(var(--main-text)) !important;
            --paper-tooltip-background: rgb(var(--main-color)) !important;
            --yt-spec-commerce-badge-background: rgba(var(--button-color), var(--button-opacity, 0.10)) !important;
            --yt-spec-commerce-tonal-hover: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
            --yt-spec-brand-button-background: var(--red) !important;
            --divider-color: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
            --yt-spec-brand-link-text: var(--red) !important;
            --yt-spec-static-grey: rgba(var(--white, var(--main-background)), 0.2) !important;
            --yt-deprecated-white-opacity-lighten-4: rgba(var(--button-color), var(--button-opacity, 0.10)) !important;
        }

        html,
html[dark] {
            background-color: rgb(var(--main-background)) !important;
        }

        html[darker-dark-theme],
html[darker-dark-theme] [dark] {
            --yt-spec-text-primary: rgb(var(--main-text)) !important;
            --yt-spec-text-primary-inverse: rgb(var(--black, var(--white, var(--main-background)))) !important;
        }

        html[system-icons],
html[system-icons] [dark] {
            --yt-spec-icon-disabled: rgba(var(--main-text), 0.38) !important;
            --yt-spec-brand-icon-inactive: rgb(var(--main-text)) !important;
            --yt-spec-icon-inactive: rgb(var(--main-text)) !important;
        }

        /* Text selection */
        ::selection {
            color: rgb(var(--black, var(--white, var(--main-background)))) !important;
            background: rgb(var(--main-color)) !important;
        }

        /* Tooltip (Old) */
        .tp-yt-paper-tooltip[style-target=tooltip],
/* Tooltip (New) */
        .ytTooltipContainerDefaultTooltipContent {
            color: rgb(var(--black, var(--white, var(--main-background))));
        }

        /* Tooltip (New) */
        .ytTooltipContainerDefaultTooltipContent {
            background:  rgb(var(--main-color));
        }

        /* Input fields */
        ytd-masthead ::-webkit-input-placeholder {
            color: rgba(var(--main-text), 0.68) !important;
        }

        ytd-searchbox[has-focus] #container.ytd-searchbox {
            border: none;
            box-shadow: none;
        }

        .focused-line.tp-yt-paper-input-container {
            border-color: rgb(var(--main-color));
        }

        .ytStandardsTextareaShapeTextAreaOutline {
            border-color: rgba(var(--button-color), var(--button-hover-opacity, 0.20));
        }

        .ytStandardsTextareaShapeLabel {
            color: rgba(var(--main-text), 0.68);
        }

        .ytStandardsTextareaShapeTextArea {
            color: rgb(var(--main-text));
        }

        /* Dropdown menus */
        ytd-dropdown-renderer {
            --paper-dropdown-menu-focus-color: rgb(var(--main-color));
        }

        .ytContextualSheetLayoutHost {
            background: var(--button-solid-color);
        }

        .yt-list-item-view-model__container--tappable:hover {
            background: rgba(var(--button-color), var(--button-hover-opacity, 0.20));
        }

        .yt-list-item-view-model__title {
            color: rgb(var(--main-text));
        }

        /* Dropdown menu > Header > Title */
        .ytPanelHeaderViewModelTitle,
/* Dropdown menu button > Arrow icon */
        .ytDropdownViewModelChevronDown {
            color: rgb(var(--main-text));
        }

        /* Dropdown menu > Header > Separator */
        .ytPanelHeaderViewModelHost {
            border-color: rgba(var(--button-color), var(--button-hover-opacity, 0.20));
        }

        /* Search box */
        .ytSearchboxComponentInputBox,
.ytSearchboxComponentSearchButton {
            background: rgba(var(--button-color), var(--button-opacity, 0.10)) !important;
            border-color: transparent !important;
            color: rgb(var(--main-text)) !important;
            box-shadow: none !important;
        }

        .ytSearchboxComponentClearButtonIcon {
            color: rgb(var(--main-text)) !important;
        }

        .ytSearchboxComponentClearButton:hover {
            background: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
        }

        /* Search suggestions box */
        .ytSearchboxComponentSuggestionsContainer {
            background: var(--button-solid-color) !important;
            border: 0 !important;
        }

        .ytSuggestionComponentSuggestion,
.ytSuggestionComponentPersonalizedSuggestion {
            color: rgb(var(--main-text)) !important;
        }

        .ytSuggestionComponentRemoveLink {
            color: rgb(var(--main-color)) !important;
        }

        .ytSuggestionComponentNewVideoIcon {
            background-color: rgb(var(--main-color)) !important;
        }

        .ytSuggestionComponentHighlighted,
.ytSuggestionComponentSuggestion:hover {
            background: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
        }

        .ytSuggestionComponentNewVideoText,
.ytSearchboxComponentReportButton,
.ytSuggestionComponentRemovedText {
            color: rgba(var(--main-text), 0.68) !important;
        }

        .ytSearchboxComponentSuggestionsContainerFooterGradient {
            background: linear-gradient(to bottom, transparent, var(--button-solid-color)) !important;
        }

        /* Buttons */
        .yt-spec-touch-feedback-shape__fill {
            background: currentcolor !important;
        }

        /* YT experiment: shorts container in grid format */
        .ytGridShelfViewModelGridShelfBottomButtonContainer {
            background: rgb(var(--main-background));
        }

        #masthead .yt-spec-button-shape-next--overlay,
.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--tonal {
            background: rgba(var(--button-color), var(--button-opacity, 0.10));
            color: rgb(var(--main-text));
            &:hover {
                background: rgba(var(--button-color), var(--button-hover-opacity, 0.20));
            }
        }

        .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--outline {
            color: rgb(var(--main-text));
            border-color: rgba(var(--button-color), var(--button-hover-opacity, 0.20));
            &:hover {
                background: rgba(var(--button-color), var(--button-hover-opacity, 0.20));
                border-color: transparent;
            }
        }

        .yt-spec-button-shape-next--call-to-action.yt-spec-button-shape-next--outline {
            color: rgb(var(--main-color));
            border-color: rgba(var(--button-color), var(--button-hover-opacity, 0.20));
            &:hover {
                background: rgba(var(--main-color), 0.30);
                border-color: transparent;
            }
        }

        .yt-spec-button-shape-next--size-m.yt-spec-button-shape-next--segmented-start::after {
            background: rgba(var(--button-color), var(--button-hover-opacity, 0.20));
        }

        .yt-spec-button-shape-next--disabled {
            &.yt-spec-button-shape-next--text {
                color: rgba(var(--main-text), 0.38);
            }
            &.yt-spec-button-shape-next--tonal {
                color: rgba(var(--main-text), 0.38);
                background: rgba(var(--button-color), var(--button-opacity, 0.10));
            }
            &.yt-spec-button-shape-next--filled {
                background: rgba(var(--button-color), var(--button-opacity, 0.10));
