          }

          /* Tooltip (New) */
          .ytTooltipContainerDefaultTooltipContent {
              background:  rgb(var(--main-color));
          }

          /* Input fields */
          ytd-masthead ::-webkit-input-placeholder {
              color: rgba(var(--main-text), 0.68) !important;
          }

          ytd-searchbox[has-focus] #container.ytd-searchbox {
