          /* Dropdown menu button > Arrow icon */
          .ytDropdownViewModelChevronDown {
              color: rgb(var(--main-text));
          }

          /* Dropdown menu > Header > Separator */
          .ytPanelHeaderViewModelHost {
              border-color: rgba(var(--button-color), var(--button-hover-opacity, 0.20));
          }

          /* Search box */
          .ytSearchboxComponentInputBox,
          .ytSearchboxComponentSearchButton {
              background: rgba(var(--button-color), var(--button-opacity, 0.10)) !important;
              border-color: transparent !important;
              color: rgb(var(--main-text)) !important;
              box-shadow: none !important;
          }

          .ytSearchboxComponentClearButtonIcon {
              color: rgb(var(--main-text)) !important;
          }

          .ytSearchboxComponentClearButton:hover {
              background: rgba(var(--button-color), var(--button-hover-opacity, 0.20)) !important;
          }

          /* Search suggestions box */
          .ytSearchboxComponentSuggestionsContainer {
              background: var(--button-solid-color) !important;
              border: 0 !important;
          }

          .ytSuggestionComponentSuggestion,
          .ytSuggestionComponentPersonalizedSuggestion {
              color: rgb(var(--main-text)) !important;
          }

          .ytSuggestionComponentRemoveLink {
              color: rgb(var(--main-color)) !important;
