      display: none if no-upcoming-2;
  }

  ytd-video-renderer[is-search]:has(:is([overlay-style=UPCOMING], lockup-attachments-view-model)) {
      display: none if no-upcoming-3;
  }

  [page-subtype="home"] ytd-rich-item-renderer:has(:is(.yt-badge-shape--live, .yt-badge-shape--thumbnail-live)) {
      display: none if no-live-streams;
  }

  [page-subtype="subscriptions"] :is(ytd-rich-item-renderer, ytd-item-section-renderer):has(:is(.yt-badge-shape--live, .yt-badge-shape--thumbnail-live)) {
      display: none if no-live-streams-2;
  }

  :is(yt-lockup-view-model, ytd-compact-video-renderer):has(:is(.yt-badge-shape--live, .yt-badge-shape--thumbnail-live)) {
      display: none if no-live-streams-3 !important; 
  }

  ytd-video-renderer[is-search]:has(:is(.yt-badge-shape--live, .yt-badge-shape--thumbnail-liver)) {
      display: none if no-live-streams-4;
  }

  if less-dark {
      html[dark],
      [dark] {
          --yt-spec-base-background: #181818;
          --yt-spec-general-background-b: #181818;
          --yt-frosted-glass-desktop: #181818;
      }

      html[dark],
      html[dark] ytd-masthead.shell.theater,
      ytd-masthead.shell.dark {
          background-color: #181818 !important;
      }

      html[dark] #home-container-skeleton,
      html[dark] #home-page-skeleton #guide-skeleton,
      html[dark] #home-chips {
          background: #181818;
      }

      #cinematics.ytd-watch-flexy {
          display: none;
      }
  }
}

if grid-search-results {
  @-moz-document url-prefix("https://www.youtube.com/") {
      if !videos-per-row {
          @container (min-width: 1956px) {
              #container.ytd-search,
              ytd-section-list-renderer {
                  --ytd-rich-grid-items-per-row: 6;
              }
          }

          @container (max-width: 1955px) {
              #container.ytd-search,
              ytd-section-list-renderer {
                  --ytd-rich-grid-items-per-row: 5;
              }
          }

          @container (max-width: 1629px) {
              #container.ytd-search,
              ytd-section-list-renderer {
                  --ytd-rich-grid-items-per-row: 4;
              }
          }

          @container (max-width: 1303px) {
              #container.ytd-search,
              ytd-section-list-renderer {
                  --ytd-rich-grid-items-per-row: 3;
              }
          }

          @container (max-width: 977px) {
              #container.ytd-search,
              ytd-section-list-renderer {
                  --ytd-rich-grid-items-per-row: 2;
              }
          }

          @container (max-width: 651px) {
              #container.ytd-search,
              ytd-section-list-renderer {
                  --ytd-rich-grid-items-per-row: 1;
              }
          }
      }

      /* Main search page */
      ytd-search {
          padding-left: 16px !important;
          padding-right: 16px !important;
          container-type: inline-size;

          ytd-search-pyv-renderer,
          ytd-ad-slot-renderer,
          .metadata-snippet-container-one-line.ytd-video-renderer,
          .metadata-snippet-container.ytd-video-renderer,
          #description-text.ytd-video-renderer,
          #description.ytd-channel-renderer,
          #expandable-metadata.ytd-video-renderer:not(:empty),
          ytd-exploratory-results-renderer.ytd-item-section-renderer,
          ytd-horizontal-card-list-renderer.ytd-item-section-renderer:not(:first-child),
          ytd-reel-shelf-renderer.ytd-item-section-renderer,
          ytd-shelf-renderer.ytd-item-section-renderer,
          grid-shelf-view-model /* YT experiment: shorts container in grid format */,
          #channel-name.ytd-video-renderer,
          #separator.ytd-video-meta-block,
          .yt-content-metadata-view-model__metadata-row:not(:first-child):not(:last-child) {
              display: none !important;
          }

          #secondary.ytd-two-column-search-results-renderer {
              display: none !important if no-right-sidebar;
          }

          #metadata.ytd-video-meta-block,
          #byline-container[hidden] {
              display: block !important;
          }

          #container.ytd-search {
              max-width: calc(var(--ytd-rich-grid-items-per-row) * (var(--ytd-rich-grid-item-max-width) + var(--ytd-rich-grid-item-margin)));
          }

          #header.ytd-search,
          ytd-two-column-search-results-renderer,
          #primary.ytd-two-column-search-results-renderer {
              max-width: 100% !important;
          }

          #contents > ytd-item-section-renderer,
          #contents > ytd-item-section-renderer > #contents {
              display: contents;
          }

          ytd-item-section-renderer[can-show-more]:after {
              /* Fix for elements appearing in wrong spots */
              content: "";
              width: 100%;
              min-height: 7000px;
          }

          #contents.ytd-section-list-renderer {
              display: flex;
              flex-wrap: wrap;
          }

          #contents > .ytd-item-section-renderer {
              margin-left: calc(var(--ytd-rich-grid-item-margin)/2);
              margin-right: calc(var(--ytd-rich-grid-item-margin)/2);
              width: calc(100%/var(--ytd-rich-grid-items-per-row) - var(--ytd-rich-grid-item-margin) - 0.01px);
              margin-bottom: 24px;
          }

          ytd-movie-renderer {
              width: calc(200%/var(--ytd-rich-grid-items-per-row) - var(--ytd-rich-grid-item-margin) - 0.01px) !important;
          }

          yt-did-you-mean-renderer,
          yt-showing-results-for-renderer,
          ytd-thumbnail.ytd-video-renderer,
          ytd-playlist-thumbnail.ytd-radio-renderer,
          ytd-playlist-thumbnail.ytd-playlist-renderer,
          ytd-playlist-thumbnail.ytd-show-renderer {
              min-width: 100% !important;
          }

          .yt-lockup-view-model__content-image /* Playlist related */ {
            width: 100% !important;
          }

          ytd-playlist-thumbnail.ytd-show-renderer {
            flex: 0 !important;
          }
          ytd-playlist-thumbnail.ytd-playlist-renderer {
            margin: 0 !important;
          }

          .thumbnail-container.ytd-movie-renderer {
              min-width: 0 !important;
          }

          ytd-item-section-renderer[top-spacing-zero]:first-child #contents.ytd-item-section-renderer .ytd-item-section-renderer:first-child:not(yt-did-you-mean-renderer):not(yt-showing-results-for-renderer) {
              margin-top: 16px;
          }

          #dismissible.ytd-video-renderer,
          ytd-radio-renderer,
          ytd-playlist-renderer,
          .yt-lockup-view-model--horizontal /* Playlist related */,
          #content-section.ytd-channel-renderer,
          #info-section.ytd-channel-renderer,
          ytd-show-renderer {
              flex-direction: column;
          }

          h3:not(.ytd-movie-renderer) {
              margin: 12px 0 4px 0 !important;
          }

          #video-title:not(.ytd-child-video-renderer),
          .yt-lockup-metadata-view-model__title {
              font-size: var(--dfs-1-4rem, 1.6rem) !important;
              line-height: var(--dfs-2rem, 2.2rem) !important;
              font-weight: 500 !important;
          }

          #channel-title.ytd-channel-renderer {
              font-size: var(--dfs-1-4rem, 1.6rem);
              line-height: var(--dfs-2rem, 2.2rem);
              font-weight: 500;
              margin: 12px 0 4px 0;
              align-self: center;
          }

          :is(#metadata-line, #byline-container),
          .yt-content-metadata-view-model__metadata-text /* Playlist related */,
          #metadata.ytd-channel-renderer {
              font-size: var(--dfs-1-2rem, 1.4rem) !important;
              line-height: var(--dfs-1-8rem, 2rem) !important;
              max-height: 3.6rem !important;
          }

          #content.ytd-playlist-renderer,
          #content.ytd-radio-renderer {
              flex-basis: 100%;
          }

          #info.ytd-channel-renderer {
              padding: 0 0px 16px 0;
              text-align: center;
          }

          .ytd-channel-renderer yt-formatted-string {
              text-align: center !important;
          }

          #avatar-section.ytd-channel-renderer {
              min-width: 100% !important;
          }

          .ytd-channel-renderer:is(#buttons, #purchase-button, #subscribe-button) {
              padding: 0 !important;
              align-self: center;
          }

          #badges.ytd-video-renderer {
              margin: 4px 0 0 0;
          }

          .text-wrapper.ytd-video-renderer {
              position: relative;
              width: 100%;
          }

          #channel-thumbnail.ytd-video-renderer {
              position: absolute;
              top: 12px;
              display: var(--hpp-none, block);
          }

          yt-img-shadow.ytd-video-renderer img.yt-img-shadow {
              width: 36px;
              height: 36px;
          }

          ytd-video-renderer[use-search-ui] #channel-info.ytd-video-renderer {
              padding: 0;
          }

          body[dir="ltr"] & {
              ytd-menu-renderer.ytd-video-renderer {
                  position: absolute;
                  right: -12px;
                  margin: 4px 0 0 0;
              }

              #title-wrapper.ytd-video-renderer {
                  padding-right: 24px;
              }

              .ytd-video-renderer:is(#title-wrapper, #channel-name, #badges, #buttons),
              ytd-video-meta-block.ytd-video-renderer {
                  padding-left: var(--hpp-none, 48px);
              }
          }

          body[dir="rtl"] & {
              ytd-menu-renderer.ytd-video-renderer {
                  position: absolute;
                  left: -12px;
                  margin: 4px 0 0 0;
              }

              #title-wrapper.ytd-video-renderer {
                  padding-left: 24px;
              }

              .ytd-video-renderer:is(#title-wrapper, #channel-name, #badges, #buttons),
              ytd-video-meta-block.ytd-video-renderer {
                  padding-right: var(--hpp-none, 48px);
              }
          }
      }
  }

  @-moz-document regexp("https://www.youtube.com/(@|c/).*/search.*") {
      /* Channel search page */
      #description-text.ytd-video-renderer {
          display: none;
      }

      #metadata.ytd-video-meta-block {
          display: block;
      }

      ytd-browse[page-subtype=channels] ytd-two-column-browse-results-renderer {
          width: calc(100% - 32px) !important;
          max-width: calc(var(--ytd-rich-grid-items-per-row) * (var(--ytd-rich-grid-item-max-width) + var(--ytd-rich-grid-item-margin))) !important;
          container-type: inline-size;
      }

      #contents.ytd-item-section-renderer,
      ytd-item-section-renderer.ytd-section-list-renderer {
          display: contents;
      }

      #contents.ytd-section-list-renderer {
          display: flex;
          flex-wrap: wrap;
      }

      ytd-video-renderer,
      ytd-playlist-renderer,
      ytd-show-renderer {
          width: calc(100%/var(--ytd-rich-grid-items-per-row) - var(--ytd-rich-grid-item-margin) - 0.01px);
          margin-left: calc(var(--ytd-rich-grid-item-margin)/2);
          margin-right: calc(var(--ytd-rich-grid-item-margin)/2);
          margin-bottom: 24px;
      }

      ytd-thumbnail.ytd-video-renderer,
      ytd-playlist-thumbnail.ytd-playlist-renderer,
      ytd-playlist-thumbnail.ytd-show-renderer {
          width: 100% !important;
          height: 100% !important;
          flex: 0 !important;
          &:before {
              display: block;
              content: "";
              padding-top: 56.11%;
          }
      }

      #dismissible.ytd-video-renderer,
      ytd-playlist-renderer,
      ytd-show-renderer {
          flex-direction: column;
      }

      #video-title:not(.ytd-child-video-renderer) {
          font-size: var(--dfs-1-4rem, 1.6rem) !important;
          line-height: var(--dfs-2rem, 2.2rem) !important;
          font-weight: 500 !important;
      }

      :is(#byline-container, #metadata-line) {
          font-size: var(--dfs-1-2rem, 1.4rem) !important;
          line-height: var(--dfs-1-8rem, 2rem) !important;
          max-height: 3.6rem !important;
      }

      .title-and-badge.ytd-video-renderer,
      h3:is(.ytd-playlist-renderer, .ytd-show-renderer) {
          margin: 12px 0 4px 0;
      }

      #badges.ytd-video-renderer {
          margin: 4px 0 0 0;
      }

      #content.ytd-playlist-renderer {
          flex-basis: 100%;
      }

      .text-wrapper.ytd-video-renderer {
          max-width: 100%;
      }

      body[dir="ltr"] {
          ytd-menu-renderer.ytd-video-renderer {
              position: absolute;
              right: -12px;
              margin-top: 4px;
          }

          #meta.ytd-video-renderer {
              padding-right: 24px;
          }
      }

      body[dir="rtl"] {
          ytd-menu-renderer.ytd-video-renderer {
              position: absolute;
              left: -12px;
              margin-top: 4px;
          }

          #meta.ytd-video-renderer {
              padding-left: 24px;
          }
      }
  }
}
